def expand_around_center(s, left, right):
    while left >= 0 and right < len(s) and s[left] == s[right]:
        left -= 1
        right += 1
    return left, right

def longest_palindrome(s):
    if not is_alphanumeric(s):
        return "", 0

    length = len(s)
    projection = [[0] * length for _ in range(length)]
    max_length = 1
    start = 0
    end = 0
    for i in range(length):
        if i >= 10:
            break
        for j in range(i):
