function rotateArray(arr, numRotations) {
  // Determine the last element of the array to become the first element
  let lastElement = arr[arr.length - 1];

  // Rotate the array by the defined numRotations
  for (let i = arr.length - 1; i >= 1; i--) {
    // Insert each element from the old position (i+1) to its new position (i)
    arr[i] = arr[i+1];
  }

  // Insert the last element to the front of the array
  arr[0] = lastElement;

  return arr;
}
